import React, { Component} from 'react';
import ReactSession from 'react-client-session';
ReactSession.setStoreType("localStorage");
//const [isAuthenticated, userHasAuthenticated] = useState(false);
import $ from 'jquery'; 
class Home extends Component{
    constructor(props){
        super(props);
        this.state = {
            uName: null,
            uPass: null,
            request : 'Web'
        }
    }
    
    getInputVal = e => {
        this.setState({[e.target.name] : e.target.value});
    } 
    
    submitForm = e => {
        e.preventDefault();
        $.ajax({
            url: 'http://localhost:5000/login',
            type: "POST",
            datatype: "JSON",
            data: this.state,
            beforeSend : function(){
                $('#submit').val('Loading..');
            },
            success: function(res){
                ReactSession.set("email", res);                
                console.log(res);
            },
            error:function(error){
                console.log(error);
            }
        })
    } 

    render(){
        return(
            <div className="page-content-wrapper">
                <div className="page-content">
                    <div className="page-bar">
                        <ul className="page-breadcrumb">
                            <li>
                                <a href="index.html">Home</a>
                                <i className="fa fa-circle"></i>
                            </li>
                            <li>
                                <span>Dashboard</span>
                            </li>
                        </ul>
                        <div className="page-toolbar">
                            <div id="dashboard-report-range" className="pull-right tooltips btn btn-sm" data-container="body" data-placement="bottom" data-original-title="Change dashboard date range">
                                <i className="icon-calendar"></i>&nbsp;
                                <span className="thin uppercase hidden-xs"></span>&nbsp;
                                <i className="fa fa-angle-down"></i>
                            </div>
                        </div>
                    </div><hr />
                    <section>
                        <div className="card">
                            <div className="card-head">
                                <h2>Hello,Please Fill Form</h2>
                            </div>
                            <div className="card-body">
                                <form id="formm" onSubmit={this.submitForm}>
                                    <div className="form-group">
                                        <input type="text" placeholder="User Name" name="uName" onChange={(event) => this.getInputVal(event)} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <input type="password" placeholder="Password" name="uPass" onChange={this.getInputVal}  className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <input id="submit" type="submit" value="Submit" className="form-control" name="submitForm" />
                                    </div>   
                                </form>
                                <p style={{color:"red"}}>{ReactSession.get("email")}</p>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        )
    }
}

export default Home;
