import React, { Component } from 'react';
import $ from 'jquery'; 
class Signup extends React.Component{
    constructor(){
        state = {
            form_data : {
                name : '',
                email : '',
                mobile : '',
                pass : ''
            }
        }
    }

    getInputVal = e => {
        this.setState({
            [e.target.name] : e.target.value
        });
    }

    submitForm = e => {
        e.preventDefault();
        $.ajax({
            url:"http://localhost:3000/signup",
            method:"POST",
            data:this.state.form_data,
            success: function(result){
                console.log(result);
            }
        });
    }


    render(){
        return(
            <div className="page-content-wrapper">
                <div className="page-content">
                    <div className="page-bar">
                        <ul className="page-breadcrumb">
                            <li>
                                <a href="index.html">Home</a>
                                <i className="fa fa-circle"></i>
                            </li>
                            <li>
                                <span>Dashboard</span>
                            </li>
                        </ul>
                        <div className="page-toolbar">
                            <div id="dashboard-report-range" className="pull-right tooltips btn btn-sm" data-container="body" data-placement="bottom" data-original-title="Change dashboard date range">
                                <i className="icon-calendar"></i>&nbsp;
                                <span className="thin uppercase hidden-xs"></span>&nbsp;
                                <i className="fa fa-angle-down"></i>
                            </div>
                        </div>
                    </div>
                    <hr />
                    <section>
                        <div className="card">
                            <div className="card-head">
                                <h2>Hello,Please Fill Form</h2>
                            </div>
                            <div className="card-body">
                                <form id="formm" onSubmit={this.submitForm}>
                                    <div className="form-group">
                                        <input type="text" placeholder="Full Name" name="name" onChange={(event) => this.getInputVal(event)} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <input type="text" placeholder="Email Address" name="email" onChange={(event) => this.getInputVal(event)} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <input type="text" placeholder="Mobile No." name="mobile" onChange={(event) => this.getInputVal(event)} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <input type="password" placeholder="Password" name="pass" onChange={this.getInputVal}  className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <input id="submit" type="submit" value="Submit" className="form-control" name="submitForm" />
                                    </div>   
                                </form>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        )
    }
}

export default Signup;
